mapper.select:
SELECT a.*,b.name as className,c.`name` as detail1Name,d.`name` as detail2Name,e.`name`as detail3Name from t_box_type a left JOIN t_box_classification b on a.classId=b.id left JOIN t_box_classification c on a.detail1=c.id left JOIN t_box_classification d on a.detail2=d.id left JOIN t_box_classification e on a.detail3=e.id
a.name LIKE CONCAT(CONCAT('%', #{name}),'%')
and a.classId=#{classId}
and a.boxId=#{boxId}

mapper.delete:
  delete from t_box_type
   where boxId in
   
		#{id}

mapper.select:
SELECT a.* from t_box_type a left JOIN t_box_classification b on a.classId=b.id left JOIN t_box_classification c on a.detail1=c.id left JOIN t_box_classification d on a.detail2=d.id left JOIN t_box_classification e on a.detail3=e.id where 1=1
and a.classId=#{classid}
and a.detail1=#{detail1}
and a.detail2=#{detail2}
and a.detail3=#{detail3}
