mapper.select:
	select 
	 
	from t_layout_detail 
		where 1=1 
		
        and box_id like CONCAT(CONCAT('%', #{boxId,jdbcType=VARCHAR}),'%')
	      
	      
	       and box_length = #{boxLength,jdbcType=DOUBLE}
	      
	      
	        and box_width = #{boxWidth,jdbcType=DOUBLE}
	      
	      
	       and box_highth = #{boxHighth,jdbcType=DOUBLE}
	      
	      
	       and box_unit = #{boxUnit,jdbcType=INTEGER}
	      
	      
	       and paper_length = #{paperLength,jdbcType=DOUBLE}
	      
	      
	       and paper_width = #{paperWidth,jdbcType=DOUBLE}
	      
	      
	       and paper_unit = #{paperUnit,jdbcType=INTEGER}
	      
	      
	       and paper_X_id like CONCAT(CONCAT('%',  #{paperXId,jdbcType=VARCHAR}),'%')
	      
	      
	       and xnumber = #{xnumber,jdbcType=INTEGER}
	      
	      
	       and ynumber = #{ynumber,jdbcType=INTEGER}
	      
	      
	        picture_address like CONCAT(CONCAT('%', #{pictureAddress,jdbcType=VARCHAR}),'%')
	      
	      
	       and dxf_address like CONCAT(CONCAT('%',  #{dxfAddress,jdbcType=VARCHAR}),'%')
