mapper.select:
SELECT a.*,b.name as tname,c.`name` as pname,c.cname,d.name as sname from t_technology_price a left join t_technology_detail b on a.tid=b.id left join t_technology_detail c on a.pid=c.id left join t_technology_detail d on a.style=d.id
b.name LIKE CONCAT(CONCAT('%', #{map.name}),'%')
and a.pid=#{map.parentId}
and c.cname=#{map.cname }

mapper.delete:
  delete from t_technology_price
   where id in
   
		#{id}

mapper.insert:
insert into t_technology_price (id, tid, pid, 
  color_num, style, price, 
  unit, createby, createtime
  )
values (#{id,jdbcType=VARCHAR}, #{tid,jdbcType=VARCHAR}, #{pid,jdbcType=VARCHAR}, 
  #{colorNum,jdbcType=INTEGER}, #{style,jdbcType=VARCHAR}, #{price,jdbcType=DOUBLE}, 
  #{unit,jdbcType=VARCHAR}, #{createby,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )
